import { createStore, applyMiddleware } from "redux";
import { composeWithDevTools } from "redux-devtools-extension";
import thunk from "redux-thunk";

const initialState = {
  loggedInUser: "",
  isAuthenticaticated: false,
  GetPortfolios: null,
  jwt: ""
};

export const actionTypes = {
  AUTH_USER: "AUTH_USER",
  LOGOUT: "LOGOUT"
};

// REDUCERS
export const reducer = (state = initialState, action) => {
  switch (action.type) {
    case AUTH_USER: {
      const newState = {
        ...state,
        loggedInUser: action.payload.loggedInUser,
        isAuthenticaticated: true,
        jwt: action.payload.jwt,
        role: action.payload.role
      };
      return newState;
    }
    case LOGOUT: {
      return initState;
    }
    // case "GET_PORTFOLIOS": {
    //   // console.log(action.payload.GetPortfolios);
    //   return {
    //     // ...state,
    //     GetPortfolios: action.payload.GetPortfolios
    //   };
    // }
    // case "REMOVE_PORTFOLIOS": {
    //   console.log(state)
    //   return {
    //     ...state,
    //     GetPortfolios: state.GetPortfolios.filter(
    //       item => item.id === action.payload
    //     )
    //   };
    // }
    default: {
      return state;
    }
  }
};

// ACTIONS
export const serverRenderClock = () => {
  return { type: actionTypes.TICK, light: false, ts: Date.now() };
};
export const startClock = () => {
  return { type: actionTypes.TICK, light: true, ts: Date.now() };
};

export const incrementCount = () => {
  return { type: actionTypes.INCREMENT };
};

export const decrementCount = () => {
  return { type: actionTypes.DECREMENT };
};

export const resetCount = () => {
  return { type: actionTypes.RESET };
};

export function initializeStore(initialState = exampleInitialState) {
  return createStore(
    reducer,
    initialState,
    composeWithDevTools(applyMiddleware(thunk))
  );
}
