import React from "react";
import "../static/styles/_portfolios.scss";
import BaseLayout from "../components/BaseLayout";
import Portfolio_landing_page_Card from "../components/Portfolios/Portfolio_landing_page_Card";
import useAuth from "../components/Auth/useAuth";
import { Spinner } from "react-bootstrap";
import { useGetPortfolios } from "../components/CustomHooks";

function Portfolios(props) {
  const auth = useAuth();
  const dataPort = useGetPortfolios();
  const [data, setData] = React.useState(null);

  React.useEffect(() => {
    if (dataPort !== null) {
      if (data === null) {
        setData(dataPort.data);
      }
    }
  }, [dataPort]);

  function ReRender(id) {
    setData({
      GetPortfolios: data.GetPortfolios.filter(item => item.id !== id)
    });
  }
  return (
    <BaseLayout tab="Protfolios">
      <div className="main_wrapper_port m-0 p-0">
        <div className="portfolios_title">
          <h1>My Portfolios</h1>
        </div>
        <div className="portfolios_card_wrapper mb-0 p-3">
          {data !== null &&
            data.GetPortfolios.map((portfolio, id) => {
              return (
                <Portfolio_landing_page_Card
                  auth={auth}
                  key={id}
                  portfolio={portfolio}
                  ReRender={ReRender}
                />
              );
            })}
          {data === null && (
            <Spinner animation="border" role="status">
              <span className="sr-only">Loading...</span>
            </Spinner>
          )}
        </div>
      </div>
      <style jsx>{`
        // -------------- main wrapper -------------
        .main_wrapper_port {
          position: relative;
          min-height: 100vh;
          // ------------------- gradient -----------------------
          // background-color: #485461;
          background-color: #e9ecef;
          //   background: radial-gradient(
          //       circle closest-corner at center,
          //       rgba(34, 34, 34, 0.918),
          //       black 70%
          //     )
          //     no-repeat;
          //   // ------------------- gradient -----------------------
          // }
        }
        .portfolios_title {
          padding: 80px 0 0 0;
          text-shadow: 1px 2px 10px rgb(143, 142, 142);
          // color: rgb(143, 142, 142);
          font-family: "Mina";
          text-align: center;
        }
        // -------------- main wrapper -------------
      `}</style>
    </BaseLayout>
  );
}

// Portfolios.getInitialProps = async ({ req }) => {
//   const data = await fetchGraphQL(`{
//     GetPortfolios{
//      id
//     title
//       shortDescription
//       technologiesUsed
//     }
//     }`);
//   return { ...data };
// };

export default Portfolios;
