import React from "react";
import { fetchGraphQL } from "./HelperFunctions";
export function useGetPortfolios() {
  const [data, setData] = React.useState(null);
  const [dataRequested, setDataRequested] = React.useState(false);

  if (!dataRequested) {
    setDataRequested(true);
    const query = `{
      GetPortfolios{
       id
       title
       shortDescription
       technologiesUsed
      }
      }`;
    fetchGraphQL(query)
      .then(data => {
        setData({ ...data });
      })
      .catch(err => {
        console.log(err);
      });
  }
  return { data };
}
